CREATE OR REPLACE FUNCTION ksiegarnia_internetowa.isbn_check()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
declare
wyn int;
id numeric;
begin
id = new.isbn;
for i in 1..10 loop
wyn = wyn + (id%10)*i;
id = id/10;
end loop;
if wyn%11 != 0 then raise exception 'Zly numer isbn'; end if;
return new;
end;
$function$;

CREATE OR REPLACE FUNCTION ksiegarnia_internetowa.kod_check()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
begin
if new.miasto in (select m.miasto from kody_pocztowe k
join miasta m using(poczta)  where new.kod_pocztowy=k.kod_pocztowy)
then
return new;
end if;
raise exception 'Zly kod pocztowy';
end;
$function$;

CREATE OR REPLACE FUNCTION ksiegarnia_internetowa.nowe_zamowienie()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
begin
if (select ilosc_sztuk from formaty_ksiazek f where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu) is null
or (select ilosc_sztuk from formaty_ksiazek f where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu) < new.ilosc
then raise exception 'Zly tytul lub format ksiazki';
end if;
UPDATE formaty_ksiazek f SET ilosc_sztuk = ilosc_sztuk-new.ilosc where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu;
return new;
end;
$function$;

CREATE OR REPLACE FUNCTION ksiegarnia_internetowa.update_zamowienie()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
begin
if (select ilosc_sztuk from formaty_ksiazek f where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu) is null
or (select ilosc_sztuk from formaty_ksiazek f where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu) + old.ilosc < new.ilosc
then raise exception 'Zly tytul lub format ksiazki';
end if;
UPDATE formaty_ksiazek f SET ilosc_sztuk = ilosc_sztuk+old.ilosc-new.ilosc where f.id_ksiazki = new.id_ksiazki and f.id_formatu = new.id_formatu;
return new;
end;
$function$;


