CREATE OR REPLACE FUNCTION ksiegarnia_internetowa.find_similar(book text)
  returns table(tytul varchar) as
$$
begin
  return query
  select k.tytul
  from ksiazki k
  where
    ((select array_agg(id_autora)
      from autorzy_ksiazek
      where isbn = id_ksiazki)
     && (select array_agg(id_autora)
         from autorzy_ksiazek
           join ksiazki kn
             on kn.isbn = id_ksiazki
         where book = kn.tytul)
     or (select array_agg(id_gatunku)
         from gatunki_ksiazek
         where isbn = id_ksiazki)
        && (select array_agg(id_gatunku)
            from gatunki_ksiazek
              join ksiazki kn
                on kn.isbn = id_ksiazki
            where book = kn.tytul))
    and k.tytul != book
  limit 3;
end;
$$
language plpgsql;
















